import React from 'react'
import ReactDOM from 'react-dom'
import App from './App.js';



class DisplayList extends React.Component{
    // constructor(){
    //     super();
    //     this.state = {
    //       cart: initialState
    //     };

    //   }
    state = {
        cart:[],
        totalPrice:0,
        disabled : false
    }

    render(){
        return(
            // <div>
            //     {this.props.list.length > 0 ? (this.props.list.map(item => 
            // <li>{item.name}, {item.gender},{item.role}</li>)) : (
            // <div>
            //   No results found 
            // </div>
            // )

            <div>
            {this.props.list.map(item =>
            (<div>
                <h3>Name: {item.name}</h3>
                <img src={item.img} width="300px" height ="200px" />
                <h3>Gender: {item.gender}</h3>
                <h3>Role: {item.role}</h3>
                <h3>Difficulty: {item.difficulty}</h3>
                <h3>Price: ${item.price}</h3>
                <button onClick={() => this.addToCart(item)}> {this.state.disabled ? 'Add to Cart' : 'Already in Cart'}</button>
                <hr/>
                </div>

                 )
            )}

            <h2>The total number of heros in the cart: {this.state.cart.length}</h2>
            {this.state.cart.map(item =>
            (<div>
                <h3>Name: {item.name}</h3>
                <img src={item.img} width="300px" height ="200px" />
                <h3>Gender: {item.gender}</h3>
                <h3>Role: {item.role}</h3>
                <h3>Difficulty: {item.difficulty}</h3>
                <h3>Price: ${item.price}</h3>
                <button onClick={() => this.removeFromCart(item)}>Remove from Cart</button>
                <hr/>
                </div>

                 )
            )}
            <h2>The total Price is: ${this.state.totalPrice}</h2>


            </div>





        )
    }

    addToCart  = (item) => {
        if (this.state.disabled) {
            return;
        }

        this.setState(({ cart,totalPrice,disabled }) => {

            if (!cart.includes(item)) {
                return { cart: [...cart, item], totalPrice:Math.round((totalPrice+item.price)*100)/100,disabled:true }
            }
        })
    }

    removeFromCart = (item) => {
        this.setState(({ cart,totalPrice }) => {
            if (cart.includes(item)) {
                return { cart: cart.filter((i) => i !== item), totalPrice: Math.round((totalPrice-item.price)*100)/100 }
            }
        })
    }

    // addToCart = (item) => {
    //     this.setState({
    //         cart : [...this.state.cart, item]})
        // this.setState(() => {
        //     return {cart:[...this.state.cart,
        //     item] };
        //   }, () => { this.addTotals(); });


}

export default DisplayList;
