import React from 'react'
import ReactDOM from 'react-dom'
import App from './App.js';
import DisplayList from './DisplayList.js';
// import Navbar from 'react-bootstrap/Navbar'
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Navbar, Nav, NavItem, NavDropdown, MenuItem } from 'react-bootstrap';
class FilteredList extends React.Component{
    constructor(){
      super();
      this.state = {
        gender: "All",
        role: "All"
      };

    }

    render(){
        return(

          <div>
            <Navbar bg="light" expand="lg">
            <Navbar.Brand href="#home">Filter on Gender</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="mr-auto">

                <NavDropdown title="Dropdown" id="basic-nav-dropdown">
                  <NavDropdown.Item href="#action/3.1"><Nav.Link eventKey="All" onSelect={this.onSelectFilterSize}>All</Nav.Link></NavDropdown.Item>
                  <NavDropdown.Item href="#action/3.2"><Nav.Link eventKey="Male" onSelect={this.onSelectFilterSize}>Male</Nav.Link></NavDropdown.Item>
                  <NavDropdown.Item href="#action/3.3"><Nav.Link eventKey="Female" onSelect={this.onSelectFilterSize}>Female</Nav.Link></NavDropdown.Item>

                </NavDropdown>
              </Nav>

            </Navbar.Collapse>
          </Navbar>


          <Navbar bg="light" expand="lg">
            <Navbar.Brand href="#home">Filter on Role</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="mr-auto">

                <NavDropdown title="Dropdown" id="basic-nav-dropdown">
                  <NavDropdown.Item href="#action/3.1"><Nav.Link eventKey="All" onSelect={this.onSelectFilterSize}>All</Nav.Link></NavDropdown.Item>
                  <NavDropdown.Item href="#action/3.2"><Nav.Link eventKey="Support" onSelect={this.onSelectFilterSize}>Support</Nav.Link></NavDropdown.Item>
                  <NavDropdown.Item href="#action/3.3"><Nav.Link eventKey="Made" onSelect={this.onSelectFilterSize}>Made</Nav.Link></NavDropdown.Item>
                  <NavDropdown.Item href="#action/3.3"><Nav.Link eventKey="Fighter" onSelect={this.onSelectFilterSize}>Fighter</Nav.Link></NavDropdown.Item>
                  <NavDropdown.Item href="#action/3.3"><Nav.Link eventKey="Shooter" onSelect={this.onSelectFilterSize}>Shooter</Nav.Link></NavDropdown.Item>
                  <NavDropdown.Item href="#action/3.3"><Nav.Link eventKey="Jungler" onSelect={this.onSelectFilterSize}>Jungler</Nav.Link></NavDropdown.Item>

                </NavDropdown>
              </Nav>

            </Navbar.Collapse>
          </Navbar>

          <DisplayList list={this.props.list.filter(this.matchesFilterSize)}/>

          </div>

        )
    }

    onSelectFilterSize = event => {
      this.setState({
        gender: event
      })
    };

    matchesFilterSize = item => {
      // all items should be shown when no filter is selected
      if(this.state.gender === 'All') { 
        return true
      } else if (this.state.gender === item.gender) {
        return true
      } else {
        return false
      }
    }


    onSelectFilterRole = event => {
      this.setState({
        role: event
      })
    };

    matchesFilterRole = item => {
      // all items should be shown when no filter is selected
      if(this.state.role === 'All') { 
        return true
      } else if (this.state.role === item.role) {
        return true
      } else {
        return false
      }
    }


}


export default FilteredList;
