import React from 'react'
import ReactDOM from 'react-dom'
import App from './App.js';
// import Navbar from 'react-bootstrap/Navbar'
import { Button, Navbar, Nav, NavItem, NavDropdown, MenuItem } from 'react-bootstrap';
class FilteredList extends React.Component{
    constructor(){
      super();
      this.state = {
        size: "All"
      };

    }

    render(){
        return(
          <div>
            <Navbar bg="light" expand="lg">
            <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="mr-auto">
                <Nav.Link href="#home">Home</Nav.Link>
                <Nav.Link href="#link">Link</Nav.Link>
                <NavDropdown title="Dropdown" id="basic-nav-dropdown">
                  <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                  <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
                  <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
                  <NavDropdown.Divider />
                  <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
                </NavDropdown>
              </Nav>
              <Form inline>
                <FormControl type="text" placeholder="Search" className="mr-sm-2" />
                <Button variant="outline-success">Search</Button>
              </Form>
            </Navbar.Collapse>
          </Navbar>
          </div>
        // <div>
        //     <Navbar bg="light" expand="lg">
        //       <Navbar.Brand href="#home">Filter on Gender</Navbar.Brand>
        //       <Navbar.Toggle aria-controls="basic-navbar-nav" />
        //       <Navbar.Collapse id="basic-navbar-nav">

        //       <Nav className="mr-auto">
        //           <NavDropdown title="Dropdown" id="basic-nav-dropdown">
        //             <NavDropdown.Item href="#action/3.1"><Nav.Link eventKey="All" onSelect={this.onSelectFilterSize}>All</Nav.Link></NavDropdown.Item>
        //             {/* <NavDropdown.Item href="#action/3.2"><Nav.Link eventKey="All" onSelect={this.onSelectFilterSize}>All</Nav.Link></NavDropdown.Item>
        //             <NavDropdown.Item href="#action/3.3"><Nav.Link eventKey="All" onSelect={this.onSelectFilterSize}>All</Nav.Link></NavDropdown.Item> */}

        //           </NavDropdown>
        //       </Nav>
        //       </Navbar.Collapse>
        //     </Navbar>
        // </div>
        )
    }

    onSelectFilterSize = event => {
      this.setState({
        size: event
      })
    };

    matchesFilterSize = item => {
      // all items should be shown when no filter is selected
      if(this.state.size === 'All') { 

        return (
          this.props.list.map(
            ()=>{
              <h3>Name: {this.props.list.name}</h3>
            }
          )
        )
      }
      // else if (this.state.size === item.size) {
      //   return true
      // } else {
      //   return false
      // }
    }


}


export default FilteredList;
